board = [[1, 2, 3], [4, 5, 6], [7, 8, 9]]
direc = {'L': (0, -1), 'R': (0, 1), 'U': (-1, 0), 'D': (1, 0)}
def parse(step, start = (1, 1)):
    rows, cols = len(board), len(board[0])
    row, col = start
    for c in step:
        move = direc[c]
        if row + move[0] >= 0 and row + move[0] < rows:
            if col + move[1] >= 0 and col + move[1] < cols:
                row += move[0]
                col += move[1]
    print(board[row][col])
    return (row, col)

